function hasScrolled() {
    var e = $(this).scrollTop(),
        l = $siteHeader.outerHeight();

    $siteHeader.offset().top < 10 ? $siteHeader.addClass("top-of-page") : $siteHeader.removeClass("top-of-page"),
        Math.abs(lastScrollTop - e) <= delta || (e > lastScrollTop && e > l ? $siteHeader.removeClass("nav-down").addClass("nav-up") : e + $(window).height() < $(document).height() && $siteHeader.removeClass("nav-up").addClass("nav-down"),
            lastScrollTop = e)
}

var didScroll,
    lastScrollTop = 0,
    delta = 5,
    $siteHeader = $(".site-header");

$(window).scroll(function (e) {
    didScroll = !0
}), setInterval(function () {
    didScroll && (hasScrolled(), didScroll = !1)
}, 1
);